mapper.select:
SELECT * FROM (
    SELECT *

        ,
        CAST(
            (
            6371 * ACOS(
            COS(RADIANS(#{lat})) * COS(RADIANS(`booking`.`t_shop`.lat))
            * COS(RADIANS(`booking`.`t_shop`.lng) - RADIANS(#{lng})) + SIN(RADIANS(#{lat}))
            * SIN(RADIANS(`booking`.`t_shop`.lat)))
            ) AS DECIMAL(9,3)
        ) AS DISTANCE

        FROM `booking`.`t_shop`

            AND `id` like '%${id}%'
            AND `name` like '%${name}%'
            AND `address` like '%${address}%'
            AND `update_time` = #{updateTime}
            AND `create_time` = #{createTime}
              and create_time >=  #{createTimeStart}
              and create_time <=  #{createTimeEnd}
              and update_time >=  #{updateTimeStart}
              and update_time <=  #{updateTimeEnd}

) T


        AND T.DISTANCE <= #{distance}





        ,T.DISTANCE

    ,T.name
